.subproductGrid {
  display: flex;
  flex-direction: column;
  margin-left: 0;
  list-style: none;

  &__item {
    width: 100%;
    display: block;
  }

  &__card {
    @extend .row;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    margin-top: remCalc(15px);
    margin-bottom: remCalc(15px);

    @media (min-width: $screen-medium) {
      margin-top: remCalc(25px);
      margin-bottom: remCalc(25px);
      flex-direction: row;
    }

    &:nth-child(even) {
      @media (min-width: $screen-medium) {
        flex-direction: row-reverse !important;
      }
    }
  }

  &__figure,
  &__textholder {
    @extend .col;
    width: 100%;
    text-align: center;

    @media (min-width: $screen-medium) {
      width: 50%;
    }

    &.bytrend {
      width: 100%;
    }
  }

  &__figure {
    margin: 0;

    @media (max-width: $screen-medium) {
      margin-bottom: remCalc(20px);
    }

    > a {
      display: block;
    }
  }

  &__img-container {
    display: block;
    position: relative;

    &::after {
      content: "";
      padding-bottom: 124%;
      display: block;
    }
  }

  &__title {
    margin-bottom: remCalc(20px);
    line-height: 1.4;

    @media (min-width: $screen-medium) {
      margin-bottom: remCalc(25px);
    }
  }

  &__description {
    font-size: remCalc(14px);
    margin-bottom: remCalc(20px);

    @media (min-width: $screen-medium) {
      font-size: remCalc(16px);
      margin-bottom: remCalc(30px);
    }
  }

  &__button {
    display: flex;
    flex-direction: column;
    justify-content: center;

    @media (min-width: $screen-small) {
      flex-direction: row;
    }
  }
}

.page-content {
