.home-featured-categories {
  overflow: hidden;
  .container {
    padding-left: 0px;
    padding-right: 0px;
  }
  .row {
    margin-left: -1px;
    margin-right: -1px;
    @media (min-width: $screen-medium) {
      margin-left: -3px;
      margin-right: -3px;
    }
    > * {
      -ms-flex: 0 0 50%;
      flex: 0 0 50%;
      max-width: 50%;
      @media (min-width: $screen-medium) {
        -ms-flex: 0 0 50%;
        flex: 0 0 50%;
        max-width: 50%;
      }
      @media (min-width: $screen-large) {
        -ms-flex: 0 0 33.33%;
        flex: 0 0 33.33%;
        max-width: 33.33%;
      }
    }
  }
  .col {
    padding: 1px;

    @media (min-width: $screen-medium) {
      padding: 3px;
    }
  }
  .card {
    padding: 0;
    position: relative;
    &::before {
      content: "";
      padding-bottom: 155%;
      display: block;
      @media (min-width: $screen-large) {
        padding-bottom: 155%;
      }
    }
  }
  .card-img-top {
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    margin: auto;
    border: 0;
    width: 100%;
    max-height: 100%;
    -o-object-fit: cover;
    object-fit: cover;
    max-width: 100%;
    margin: 0;
    display: block;
    aspect-ratio: 9/16;
  }
  .card-body {
    position: absolute;
    // background-color: yellowgreen;
    width: 100%;
    z-index: 2;
    bottom: 0;
    padding: remCalc(18px 14px);
    @media (min-width: $screen-large) {
      padding: remCalc(36px);
    }
  }
  .card-title {
    font-size: remCalc(15px);
    color: white;
    text-transform: uppercase;
    margin-bottom: 2px;
    @media (min-width: $screen-large) {
      // font-size: remCalc(40px);
      // font-size: 2.08vw;
      font-size: 1.4vw;
    }
  }
  .card-text {
    display: none;
  }
  .btn-link {
    font-weight: 400;
    color: white;
    text-decoration: none;
    text-transform: uppercase;
    text-decoration: none;
    box-shadow: 0px 1px 0px rgba(255, 255, 255, 1);
    font-size: remCalc(10px);
    transition: box-shadow ease-in-out 300ms;

    @media (min-width: $screen-large) {
      font-size: remCalc(12px);
    }
  }
  .card {
    &:hover,
    &:focus,
    &:focus-visible {
      .btn-link {
        box-shadow: 0px 1px 0px rgba(255, 255, 255, 0);
      }
    }
  }
}

