.home-ad-banner-three {
  div[class^="sd-hero-image"],
  div[class*=" sd-hero-image"] {
    > div {
      > div {
        &:nth-child(1) {
          justify-content: flex-end;
          padding: 0;
          align-items: stretch;
          // min-height: 590px;
          min-height: auto;
          padding-bottom: 56%;
          @media (min-width: $screen-mini-large) {
            min-height: 340px;
            // min-height: auto;
            padding-bottom: 0;
          }
          @media (min-width: $screen-large) {
            min-height: 590px;
          }

          @media (min-width: $screen-large) {
            // min-height: 590px;
            min-height: 76vh;
          }

          // @media (min-resolution: 3840x2160) {
          //   min-height: 80vh;
          // }
          @media only screen and (min-width: 3840px) and (orientation: landscape) {
            min-height: 80vh;
          }
          &::after {
            content: "";
            width: 100%;
            @media (min-width: $screen-medium) {
              width: calc(100%);
              // background-color: red;
            }
            @media (min-width: $screen-mini-large) {
              width: calc(100% - 480px);
            }
          }
          > div {
            background-color: $clr-grey-light-twenty-two;
            max-width: 100%;
            padding: 0 8rem;
            @media (min-width: $screen-medium) {
              width: 100%;
              // padding: 0 8rem;
              // -webkit-box-flex: 0;
              // -ms-flex: 0 0 480px;
              // flex: 0 0 480px;
              // max-width: 480px;
            }

            @media (min-width: 800px) {
              display: none !important;
            }
            @media (min-width: $screen-mini-large) {
              display: flex !important;
              padding: 0 8rem;
              -webkit-box-flex: 0;
              -ms-flex: 0 0 480px;
              flex: 0 0 480px;
              max-width: 480px;
            }
            [data-test-id="hero-image-title"] {
              color: black;
              font-family: $font-family-regular;
              font-size: remCalc(26px);
