.pagination-list {
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
  padding-left: remCalc(56px);
  padding-right: remCalc(56px);
  margin-bottom: 0;

  @media (max-width: $screen-mini-large-down) {
    padding-left: remCalc(0);
    padding-right: remCalc(0);
    padding-bottom: remCalc(40px);
  }

  li {
    @media (max-width: $screen-medium-down) {
      margin-left: 0;
      margin-right: 0;
    }
  }

  .pagination-item {
    border-radius: 50%;
    width: remCalc(30px);
    height: remCalc(30px);
    line-height: 1;
    border: none;
    margin: 0 0.3em;
    @media (min-width: $screen-medium) {
      width: remCalc(35px);
      height: remCalc(35px);
    }
    &:hover,
    &:focus {
      background-color: $clr-grey-light-nine;
      color: black;
    }

    &--current {
      border-color: none;
      border-radius: 50%;
      background-color: $clr-grey-dark-two;

      &:hover,
      &:focus {
        border-color: none;
        color: white;
        background-color: $clr-grey-dark-two;
      }

      // @media (max-width: $screen-medium-down) {
      //   margin-left: 0;
      //   margin-right: 0;
      // }
    }

    &.pagination-item--next,
    &.pagination-item--previous {
      border-radius: 0px;
      position: absolute;
      text-transform: uppercase;
      width: auto;
      height: auto;

      &:hover {
        background-color: transparent;
      }

      @media (max-width: $screen-mini-large-down) {
        bottom: 0;
        width: remCalc(90px);
      }
    }

    &.pagination-item--next {
      right: 50%;
      transform: translateX(100%);

      @media (min-width: $screen-mini-large) {
        right: 0;
        transform: none;
      }
    }

    &.pagination-item--previous {
      left: 50%;
      transform: translateX(-100%);

      @media (min-width: $screen-mini-large) {
        left: 0;
        transform: none;
      }
    }
  }
}

.pagination-item {
  width: remCalc(34px);
  height: remCalc(34px);
  border-radius: 50%;
  font-size: remCalc(16px);
  font-weight: normal;

  @media (min-width: $screen-medium) {
    font-size: remCalc(18px);
  }

  &--current {
    border-color: $clr-grey-light-nine;
    border-radius: 50%;
  }
}

.pagination-list li {
  a {
    &.pagination-link {
      width: 100%;
      height: 100%;
      color: #000;
      padding: 0;
      display: flex;
      justify-content: center;
      align-items: center;
      border-radius: 0px;
      font-size: remCalc(14px);
      line-height: 1.1;
      @media (min-width: $screen-medium) {
        font-size: remCalc(18px);
      }
    }
  }

  &.pagination-item--current {
    a {
      &.pagination-link {
        font-weight: 700;
        color: white;
      }
    }
  }

  &.pagination-item--next,
  &.pagination-item--previous {
    a {
      &.pagination-link {
        width: auto;
        height: auto;
        color: #000;
        padding: 0;
        display: flex;
        justify-content: center;
        align-items: center;

        @media (max-width: $screen-medium-down) {
          font-size: 0.9em;
        }
        &:hover,
        &:focus {
          .icon > svg {
            fill: black;
          }
        }
      }
    }
  }

  &.pagination-item--next {
    a {
      &.pagination-link {
        @media (max-width: $screen-medium-down) {
          justify-content: flex-start;
          // background-color:red;
          padding-left: remCalc(10px);
        }
      }
    }
  }

  &.pagination-item--previous {
    a {
      &.pagination-link {
        @media (max-width: $screen-medium-down) {
          justify-content: flex-end;
          padding-right: remCalc(10px);
          // background-color: yellow;
        }
      }
    }
  }
}

.pagination-item--next,
.pagination-item--previous {
  .icon {
    margin-top: 0;
  }
}

.navList-action--checkbox::before {
  border-radius: 0px;
