.navList-action--checkbox:before,
.navList-action--checkbox:after {
  transform: translateY(calc(-50% - 1px));
}

.navList-action--checkbox:before {
  margin-top: 0;
  width: remCalc(8px);
  height: remCalc(8px);
  border-color: $clr-grey-light-sixteen;
  border-radius: 50%;
  left: 0px;
}

.navList-action--checkbox:after {
  background-image: none;
  background-color: black;
  font-size: 0px;
  width: remCalc(8px);
  height: remCalc(8px);
  border-color: black;
  opacity: 0;
  margin-top: 0;
  left: 0px;
  border-radius: 50%;
}

.navList-action--checkbox.is-checked::after,
.navList-action--checkbox.is-selected::after {
  transform: none;
  transform: translateY(calc(-50% - 1px));
}

