.outfit-loader {
  // position: relative;
  position: absolute;
  // top: auto;
  top: -16px;
  font-size: 12px;
  width: 100%;
  text-align: center;
  left: 0;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  bottom: auto;
  font-size: 10px;
  margin-bottom: 3px;

  @media (min-width: $screen-medium) {
    // top: 25px;
    font-size: 12px;
    /* background: red; */
    top: -20px;
  }

  @media (min-width: $screen-large) {
    // top: 25px;
  }
  // .modal & {
  //   top: 15px;
  //   top: 3px;
  // }
  &::after {
    display: block;
    content: "";
    background-image: url(../img/loading.gif);
    background-repeat: no-repeat;
    background-position: center center;
    transform: translateY(-50%) rotate(0deg);
    animation: none;
    border: none;
    transform-style: unset;
    width: 14px;
    height: 14px;
    border-radius: 0px;
    content: "";
    background-size: contain;
    transform: none;
    display: inline-block;
