.filters-options {
  display: flex;
  background-color: white;

  @media (min-width: $screen-medium) {
    gap: 40px;
  }
  @media (max-width: $screen-medium-down) {
    padding: 0.9375rem 0.9375rem 0 0.9375rem;
    flex-direction: column;
    max-height: 480px;
    overflow-y: auto;
  }

  &__block {
    // background-color: violet;
    @media (max-width: $screen-medium-down) {
      border-bottom: 1px #ededed solid;
      position: relative;
    }
    @media (min-width: $screen-medium) {
      position: relative;
    }
  }
  &__dropdown {
    &-link {
      display: block;
      position: relative;
      padding: 0.75rem 20px 0.75rem 0;
      font-size: 14px;
      margin-right: 0;
      text-decoration: none;
      background: transparent;
      font-weight: 400;
      margin-bottom: 0;
      line-height: 1.5;
      -ms-box-shadow: none;
      -webkit-box-shadow: none;
      box-shadow: none;
      background-color: white;
      @media (min-width: $screen-medium) {
        padding: 5px 20px 5px 0;
        letter-spacing: 0.026em;
      }
      @media (max-width: $screen-medium-down) {
        width: 100%;
        padding-right: 30px;
        text-align: left;
      }
      &::before {
        right: 3px;
        left: auto;
        position: absolute;
        margin-top: 0;
        content: "";
        border-right: 1px solid #333;
        border-bottom: 1px solid #333;
        display: inline-block;
        transition: all 0.1s ease-in-out;
        transform-origin: center center;
        transform: translateY(calc(-50% - 2px)) rotate(45deg);
        width: 7px;
        height: 7px;
        top: 50%;
        // margin-top: -5px;
        @media (max-width: $screen-medium-down) {
          right: 12px;
        }
      }

      &.is-open {
        &::before {
          transform: translateY(calc(-50% + 2px)) rotate(-135deg);
        }
      }
      // [data-dropdown="facetedSearch-content--brand"] {
      //   + .filters-options--dropdown-menu {
      //     background-color: red;
      //     min-width: 300px;
      //   }
      // }
      // + .filters-options--dropdown-menu[data-dropdown="facetedSearch-content--brand"] {
      //   background-color: red;
      //   min-width: 300px;
      // }
    }
    &-title {
      font-size: 0.9375rem;
      margin: 0;
      color: black;
    }
  }
  &--dropdown-menu {
    background-color: #fff;
    padding: 20px;
    @media (min-width: $screen-medium) {
      border-radius: 8px;
      box-shadow: 0 0 12px rgba(0, 0, 0, 0.16);
      margin-top: 10px;
      min-width: 220px;
      left: 0px !important;

      &.is-open {
        z-index: 100;
      }
      &::before {
        content: "";
        position: absolute;
        bottom: calc(100% - 5px);
        left: 20px;
        width: 10px;
        height: 10px;
        background-color: white;
        -ms-box-shadow: -2px -4px 4px rgba(0, 0, 0, 0.12);
        -webkit-box-shadow: -2px -4px 4px rgba(0, 0, 0, 0.12);
        box-shadow: -2px -4px 4px rgba(0, 0, 0, 0.12);
        -ms-transform: rotate(45deg);
        -webkit-transform: rotate(45deg);
        transform: rotate(45deg);
      }
    }
    @media (max-width: $screen-medium-down) {
      // margin-top: 0;
      position: relative !important;
      top: 0px !important;
      left: 0px !important;
      width: 100% !important;
      // box-shadow: none;
      // border-radius: 0px;
      padding: 0.9375rem;
    }
  }
}

.filters-options {
  .filters-options__block {
    @media (min-width: 768px) and (max-width: 1199.98px) {
      &:last-child {
        background-color: black;
        .filters-options--dropdown-menu {
          left: auto !important;
          right: 0 !important;
          &::before {
            left: auto;
            right: 20px;
          }
        }
      }
    }
  }
}

