components/products/card-home:
{{inject "thisProdIdC" id}}

<article class="card {{#if alternate}}card--alternate{{/if}} pb-0" {{#if settings.data_tag_enabled}} data-event-type="{{event}}" data-entity-id="{{id}}" data-position="{{position}}" data-name="{{name}}" data-product-category="{{#each category}}{{#if @last}}{{this}}{{else}}{{this}}, {{/if}}{{/each}}" data-product-brand="{{brand.name}}" data-product-price="{{#if price.with_tax}}{{price.with_tax.value}}{{else}}{{price.without_tax.value}}{{/if}}"  {{/if}}>
    <figure class="card-figure">

        <a href="javascript:void(0);" class="addToWishListFromList addToWishListFromList-{{id}} card-wishlisticn" data-pro-id="{{id}}" >
            <div class="loaderimageWishList-{{id}} loadingOverlay loadingOverlay--wishlist">
                <svg class="svg-full" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 19.96 17.97">
                    <style type="text/css">
                      .svgAnime { 
                        transform-origin: center center;
                        animation: x .5s ease infinite; 
                      }

                      @keyframes x {
                         from { transform: scale(.8); }
                           to { transform: scale(1); }
                      }
                  </style>
                    <path class="svgAnime"
                      d="M9.98 3.17h0C10.94 1.56 12.19.6 14.24.6c3.07 0 5.12 2.44 5.12 5.26 0 2.71-1.46 5.26-3.49 7.26-1.96 1.92-4.21 3.3-5.88 4.18-1.68-.88-3.93-2.26-5.88-4.18-2.03-2-3.49-4.55-3.49-7.26C.62 3.04 2.67.6 5.74.6c2.03 0 3.28.96 4.24 2.57 0 0 0 0 0 0z"
                      fill="none" stroke="#fffff" stroke-width="1.5" stroke-miterlimit="10" /></svg>

            </div>
            <svg xmlns="http://www.w3.org/2000/svg" class="icn-wishlist list-home" viewBox="0 0 19.96 17.97">
                <path d="M9.98 3.17h0C10.94 1.56 12.19.6 14.24.6c3.07 0 5.12 2.44 5.12 5.26 0 2.71-1.46 5.26-3.49 7.26-1.96 1.92-4.21 3.3-5.88 4.18-1.68-.88-3.93-2.26-5.88-4.18-2.03-2-3.49-4.55-3.49-7.26C.62 3.04 2.67.6 5.74.6c2.03 0 3.28.96 4.24 2.57 0 0 0 0 0 0z"  class="icn-wishlist__heart"  stroke-width="1.5" stroke-miterlimit="10"/></svg>
        </a>
        <a href="javascript:void(0);" class="removeFromWishListFromList removeFromWishListFromList-{{id}} card-wishlisticn wishlisted" data-pro-id="{{id}}" style="display: none;">
            <div class="loaderimageRemoveWishList-{{id}} loadingOverlay loadingOverlay--wishlist">
                <svg class="svg-full" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 19.96 17.97">
                    <style type="text/css">
                      .svgAnime { 
                        transform-origin: center center;
                        animation: x .5s ease infinite; 
                      }

                      @keyframes x {
                         from { transform: scale(.8); }
                           to { transform: scale(1); }
                      }
                    </style>
                    <path class="svgAnime"
                      d="M9.98 3.17h0C10.94 1.56 12.19.6 14.24.6c3.07 0 5.12 2.44 5.12 5.26 0 2.71-1.46 5.26-3.49 7.26-1.96 1.92-4.21 3.3-5.88 4.18-1.68-.88-3.93-2.26-5.88-4.18-2.03-2-3.49-4.55-3.49-7.26C.62 3.04 2.67.6 5.74.6c2.03 0 3.28.96 4.24 2.57 0 0 0 0 0 0z"
                      fill="none" stroke="#fffff" stroke-width="1.5" stroke-miterlimit="10" />
                </svg>
            </div>
            <svg xmlns="http://www.w3.org/2000/svg" class="icn-wishlist test3" viewBox="0 0 19.96 17.97">
                <path d="M9.98 3.17h0C10.94 1.56 12.19.6 14.24.6c3.07 0 5.12 2.44 5.12 5.26 0 2.71-1.46 5.26-3.49 7.26-1.96 1.92-4.21 3.3-5.88 4.18-1.68-.88-3.93-2.26-5.88-4.18-2.03-2-3.49-4.55-3.49-7.26C.62 3.04 2.67.6 5.74.6c2.03 0 3.28.96 4.24 2.57 0 0 0 0 0 0z"  class="icn-wishlist__heart"  stroke-width="1.5" stroke-miterlimit="10"/></svg>
           </a>


        {{#or price.sale_price_with_tax.value price.sale_price_without_tax.value}}
            {{#if theme_settings.product_sale_badges '===' 'sash'}}
                <div class="sale-flag-sash">
                    <span class="sale-text">{{{theme_settings.pdp_sale_badge_label}}}</span>
                </div>
            {{else if theme_settings.product_sale_badges '===' 'topleft'}}
                <div class="sale-flag-side">
                    <span class="sale-text">{{{theme_settings.pdp_sale_badge_label}}}</span>
                </div>
            {{else if theme_settings.product_sale_badges '===' 'burst'}}
                <div class="starwrap">
                    <div class="sale-text-burst">{{{theme_settings.pdp_sale_badge_label}}}</div>
                    <div class="sale-flag-star"></div>
                </div>
            {{/if}}
        {{/or}}


        <a href="{{url}}" 
        class="card-figure__link"
        aria-label="{{name}},{{> components/products/product-aria-label}}"
        {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}}
     >
         <div class="card-img-container">
             {{> components/common/responsive-img
                 image=image
                 class="card-image"
                 fallback_size=theme_settings.productgallery_size
                 lazyload=theme_settings.lazyload_mode
                 default_image=theme_settings.default_image_product
             }}
         </div>
     </a>
            {{#filter custom_fields 'Flag' property='name' }}
                <span class="card-stickers">{{value}}</span>
            {{/filter}}
            {{#filter custom_fields 'Flag Black' property='name' }}
                <span class="card-stickers" style="background: black; color: white;">{{value}}</span>
            {{/filter}}
            {{#filter custom_fields 'Flag Red' property='name' }}
                <span class="card-stickers" style="background: #751818; color: white;">{{value}}</span>
            {{/filter}}

    </figure>

</article>

components/products/product-aria-label:
{{#and price.price_range (if theme_settings.price_ranges '==' true)}}
{{#and price.price_range.min.with_tax price.price_range.max.with_tax}}
{{lang 'category.shop_by_price_range_aria' from=price.price_range.min.with_tax.formatted to=price.price_range.max.with_tax.formatted}}
{{else}}
{{lang 'category.shop_by_price_range_aria' from=price.price_range.min.without_tax.formatted to=price.price_range.max.without_tax.formatted}}
{{/and}}
{{else}}
{{#if price.with_tax}}
{{#if price.non_sale_price_with_tax}}
{{theme_settings.pdp-non-sale-price-label}}{{price.non_sale_price_with_tax.formatted}},
{{theme_settings.pdp-sale-price-label}}{{price.with_tax.formatted}}
{{else}}
{{price.with_tax.formatted}}                        
{{/if}}
{{else}}
{{#if price.non_sale_price_without_tax}}
{{theme_settings.pdp-non-sale-price-label}}{{price.non_sale_price_without_tax.formatted}},
{{theme_settings.pdp-sale-price-label}}{{price.without_tax.formatted}}
{{else}}
{{price.without_tax.formatted}}
{{/if}}
{{/if}}
{{/and}}
